import React, { useState, useRef, useEffect } from 'react'
import './App.scss'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons'
import images from './assets/images'

function App () {
  const [showPassword, setShowPassword] = useState(false)
  const [isIconVisible, setIsIconVisible] = useState(false)
  const containerRef = useRef(null)

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword)
  }
  const handleClickOutside = e => {
    if (containerRef.current && !containerRef.current.contains(e.target)) {
      setIsIconVisible(true)
    } else {
      setIsIconVisible(false)
    }
  }
  useEffect(() => {
    document.addEventListener('onMousedown', handleClickOutside)
    console.error(isIconVisible)
    return () => {
      document.removeEventListener('onMousedown', handleClickOutside)
      console.error(isIconVisible)
    }
  }, [isIconVisible])

  return (
    <div className='container' ref={containerRef}>
      <div className='dualscreen1'>ahihi</div>
      <div className='dualscreen2'>
        <div className='divlogin'>
          <h1>Đăng Nhập</h1>
          <div className='divemail'>
            <input className='email' placeholder='' />
            <label className='label'>Email</label>
          </div>
          <div className='divpassword'>
            <div className='divippass'>
              <input
                className='password'
                type={showPassword ? 'text' : 'password'}
                placeholder=''
                onFocus={() => setIsIconVisible(true)}
              />
              <label className='label'>Password</label>
              {isIconVisible && (
                <button className='eye' onClick={togglePasswordVisibility}>
                  <FontAwesomeIcon icon={showPassword ? faEye : faEyeSlash} />
                </button>
              )}
            </div>
          </div>
          <button className='btnLogin'>Đăng Nhập</button>
          <div className='divcachkhac'>
            <button className='btnfacebook'>
              <img src={images.facebook} alt='' />
            </button>
            <button className='btngoogle'>
              <img src={images.google} alt='' />
            </button>
          </div>
          <div className='divRegister'>
            <h3>Bạn chưa có tài khoản?</h3>
            <h3 className='register'>Đăng ký</h3>
          </div>
        </div>
      </div>
    </div>
  )
}

export default App
